  name: "home",
  components: {
    alreadyTask
  },
  data() {
    return {
      dateTarget: [],
      nowBox: 0,
      createItem: [false, false, false],
      createTargetName: "",
      checked: false,
      checkBox: false,
      editeTargetName: "",
      pull: { a: -1, b: -1 }, //鼠标最后经过那个item
      isEdite: { a: -1, b: -1 },
      // mouseOver: false,   //鼠标经过控制删除和编辑按钮
      cardList: [
        {
          listId: "今日计划",
          target: [
            {
              name: "the first",
              status: false,
              isDelete: false,
              completeDate: '',
              chooseList: 0,
              mouseOver: false
            },
            {
              name: "the second",
